import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMapEvents, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import '../styles/GlobalTemperatureMap.css';

// Fix for Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Custom temperature icon based on temperature value
const getTemperatureIcon = (temperature) => {
  // Define colors based on temperature ranges
  let color;
  if (temperature < -10) color = '#0022ff'; // Very cold
  else if (temperature < 0) color = '#0066ff'; // Cold
  else if (temperature < 10) color = '#00aaff'; // Cool
  else if (temperature < 20) color = '#00ffaa'; // Mild
  else if (temperature < 30) color = '#ffaa00'; // Warm
  else if (temperature < 40) color = '#ff6600'; // Hot
  else color = '#ff0000'; // Very hot

  return L.divIcon({
    className: 'temperature-icon',
    html: `<div style="background-color: ${color}; width: 100%; height: 100%; border-radius: 50%; display: flex; align-items: center; justify-content: center; color: white; font-weight: bold;">${Math.round(temperature)}Â°</div>`,
    iconSize: [30, 30],
    iconAnchor: [15, 15],
    popupAnchor: [0, -15]
  });
};

// Component to handle map clicks and fetch weather data
const MapClickHandler = ({ onMapClick }) => {
  useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      onMapClick(lat, lng);
    }
  });
  return null;
};

const GlobalTemperatureMap = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedCity, setSelectedCity] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const mapRef = useRef(null);

  // Major cities with their coordinates for initial display
  const majorCities = [
    { name: 'New York', lat: 40.7128, lng: -74.0060 },
    { name: 'London', lat: 51.5074, lng: -0.1278 },
    { name: 'Tokyo', lat: 35.6762, lng: 139.6503 },
    { name: 'Sydney', lat: -33.8688, lng: 151.2093 },
    { name: 'Cairo', lat: 30.0444, lng: 31.2357 },
    { name: 'Rio de Janeiro', lat: -22.9068, lng: -43.1729 },
    { name: 'Moscow', lat: 55.7558, lng: 37.6173 },
    { name: 'Delhi', lat: 28.6139, lng: 77.2090 }
  ];

  // Fetch weather data for initial cities on component mount
  useEffect(() => {
    const fetchInitialWeatherData = async () => {
      setLoading(true);
      try {
        const promises = majorCities.map(city => 
          fetchWeatherForLocation(city.lat, city.lng, city.name)
        );

        const results = await Promise.all(promises);
        setWeatherData(results.filter(data => data !== null));
        setLoading(false);
      } catch (err) {
        console.error('Error fetching initial weather data:', err);
        setError('Failed to load initial weather data');
        setLoading(false);
      }
    };

    fetchInitialWeatherData();
  }, []);

  // Function to fetch weather data for a specific location
  const fetchWeatherForLocation = async (lat, lng, cityName = null) => {
    try {
      // In a real app, you would use an actual API key
      // For demo purposes, we're using OpenWeatherMap API
      const apiKey = 'demo_key'; // Replace with your actual API key in production

      // For development, we'll use mock data instead of making actual API calls
      // In production, use a real weather API like:
      // const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${apiKey}`);
      // return processWeatherApiResponse(response.data, lat, lng);

      // Simulate API call delay for realism
      await new Promise(resolve => setTimeout(resolve, 600));

      // Generate realistic mock weather data based on location and current time
      const mockResponse = getRealTimeWeatherData(lat, lng, cityName);

      return {
        id: Date.now() + Math.random(),
        location: {
          name: mockResponse.name,
          lat: lat,
          lng: lng
        },
        weather: {
          temperature: mockResponse.main.temp,
          feelsLike: mockResponse.main.feels_like,
          humidity: mockResponse.main.humidity,
          pressure: mockResponse.main.pressure,
          description: mockResponse.weather[0].description,
          icon: mockResponse.weather[0].icon,
          // Add more detailed weather information
          cloudiness: mockResponse.clouds?.all || Math.floor(Math.random() * 100),
          visibility: mockResponse.visibility || (10 - Math.random() * 5) * 1000,
          rainChance: mockResponse.rain_chance || Math.floor(Math.random() * 100)
        },
        wind: {
          speed: mockResponse.wind.speed,
          direction: mockResponse.wind.deg,
          gust: mockResponse.wind.gust || mockResponse.wind.speed + (Math.random() * 5)
        },
        sun: {
          sunrise: mockResponse.sys?.sunrise || new Date(new Date().setHours(6, Math.floor(Math.random() * 30), 0)).toISOString(),
          sunset: mockResponse.sys?.sunset || new Date(new Date().setHours(18, Math.floor(Math.random() * 30), 0)).toISOString()
        },
        timestamp: new Date().toISOString(),
        // Add forecast data
        forecast: mockResponse.forecast || generateMockForecast(lat, lng)
      };
    } catch (err) {
      console.error('Error fetching weather data:', err);
      return null;
    }
  };

  // Function to generate a mock weather forecast for the next few days
  const generateMockForecast = (lat, lng) => {
    const forecast = [];
    const currentDate = new Date();
    const baseTemp = getBaseTemperatureForLocation(lat);

    // Generate forecast for next 5 days
    for (let i = 1; i <= 5; i++) {
      const forecastDate = new Date(currentDate);
      forecastDate.setDate(forecastDate.getDate() + i);

      // Add some random variation to temperature
      const tempVariation = Math.random() * 6 - 3; // -3 to +3 degrees
      const dayTemp = baseTemp + tempVariation;
      const nightTemp = dayTemp - (5 + Math.random() * 3); // 5-8 degrees cooler at night

      // Randomize weather conditions
      const conditions = [
        { description: 'clear sky', icon: '01d' },
        { description: 'few clouds', icon: '02d' },
        { description: 'scattered clouds', icon: '03d' },
        { description: 'broken clouds', icon: '04d' },
        { description: 'shower rain', icon: '09d' },
        { description: 'rain', icon: '10d' },
        { description: 'thunderstorm', icon: '11d' },
        { description: 'snow', icon: '13d' },
        { description: 'mist', icon: '50d' }
      ];

      // Weight the conditions based on temperature
      let conditionIndex;
      if (dayTemp < 0) {
        // More likely to be snow or mist when cold
        conditionIndex = Math.floor(Math.random() * 2) + 6;
      } else if (dayTemp < 10) {
        // More likely to be cloudy or rainy when cool
        conditionIndex = Math.floor(Math.random() * 5) + 2;
      } else if (dayTemp > 30) {
        // More likely to be clear or thunderstorm when hot
        conditionIndex = Math.random() < 0.7 ? 0 : 6;
      } else {
        // Random conditions for mild temperatures
        conditionIndex = Math.floor(Math.random() * conditions.length);
      }

      forecast.push({
        date: forecastDate.toISOString().split('T')[0],
        day: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][forecastDate.getDay()],
        temp: {
          day: dayTemp,
          night: nightTemp,
          min: nightTemp - Math.random() * 2,
          max: dayTemp + Math.random() * 2
        },
        weather: conditions[conditionIndex],
        humidity: Math.floor(Math.random() * 40) + 40, // 40-80%
        windSpeed: 2 + Math.random() * 8, // 2-10 m/s
        rainChance: Math.floor(Math.random() * 100) // 0-100%
      });
    }

    return forecast;
  };

  // Function to get base temperature based on latitude (simulating real-world climate patterns)
  const getBaseTemperatureForLocation = (lat) => {
    // Get current month (0-11)
    const currentMonth = new Date().getMonth();

    // Northern hemisphere summer / Southern hemisphere winter
    const isSummerNorth = currentMonth >= 4 && currentMonth <= 8;

    // Calculate base temperature based on latitude and season
    const equatorTemp = 30; // Base temperature at equator
    const poleTemp = isSummerNorth ? -10 : -30; // Base temperature at poles

    // Adjust for hemisphere and season
    const absLat = Math.abs(lat);
    const latFactor = absLat / 90;

    if ((lat >= 0 && isSummerNorth) || (lat < 0 && !isSummerNorth)) {
      // Summer in this hemisphere
      return equatorTemp - (latFactor * (equatorTemp - poleTemp + 15));
    } else {
      // Winter in this hemisphere
      return equatorTemp - (latFactor * (equatorTemp - poleTemp));
    }
  };

  // Function to generate realistic weather data based on location and current time
  const getRealTimeWeatherData = (lat, lng, cityName = null) => {
    // Generate realistic temperature based on latitude, longitude, and current season
    const baseTemp = getBaseTemperatureForLocation(lat);

    // Add daily variation based on current hour
    const hour = new Date().getHours();
    let timeOfDayVariation = 0;

    // Temperature varies throughout the day
    if (hour >= 6 && hour < 12) {
      // Morning - temperature rising
      timeOfDayVariation = (hour - 6) * 0.5;
    } else if (hour >= 12 && hour < 18) {
      // Afternoon - warmest part of day
      timeOfDayVariation = 3 - (Math.abs(hour - 15) * 0.3);
    } else if (hour >= 18 && hour < 24) {
      // Evening - temperature falling
      timeOfDayVariation = 2 - ((hour - 18) * 0.5);
    } else {
      // Night - coolest part of day
      timeOfDayVariation = -2;
    }

    // Add random variation and time of day effect
    const randomVariation = Math.random() * 4 - 2; // Random variation between -2 and 2
    const temperature = baseTemp + timeOfDayVariation + randomVariation;

    // Generate city name if not provided
    const name = cityName || `Location (${lat.toFixed(2)}, ${lng.toFixed(2)})`;

    // Weather conditions based on temperature and randomness
    let weatherCondition, weatherIcon;
    const rand = Math.random();

    if (temperature < -5) {
      if (rand < 0.7) {
        weatherCondition = 'snow';
        weatherIcon = '13d';
      } else {
        weatherCondition = 'mist';
        weatherIcon = '50d';
      }
    } else if (temperature < 5) {
      if (rand < 0.4) {
        weatherCondition = 'light snow';
        weatherIcon = '13d';
      } else if (rand < 0.7) {
        weatherCondition = 'mist';
        weatherIcon = '50d';
      } else {
        weatherCondition = 'overcast clouds';
        weatherIcon = '04d';
      }
    } else if (temperature < 15) {
      if (rand < 0.3) {
        weatherCondition = 'light rain';
        weatherIcon = '10d';
      } else if (rand < 0.6) {
        weatherCondition = 'broken clouds';
        weatherIcon = '04d';
      } else if (rand < 0.9) {
        weatherCondition = 'scattered clouds';
        weatherIcon = '03d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    } else if (temperature < 25) {
      if (rand < 0.2) {
        weatherCondition = 'light rain';
        weatherIcon = '10d';
      } else if (rand < 0.5) {
        weatherCondition = 'few clouds';
        weatherIcon = '02d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    } else if (temperature < 35) {
      if (rand < 0.15) {
        weatherCondition = 'thunderstorm';
        weatherIcon = '11d';
      } else if (rand < 0.3) {
        weatherCondition = 'few clouds';
        weatherIcon = '02d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    } else {
      if (rand < 0.2) {
        weatherCondition = 'thunderstorm';
        weatherIcon = '11d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    }

    // Adjust icon for day/night
    if (hour >= 18 || hour < 6) {
      weatherIcon = weatherIcon.replace('d', 'n');
    }

    // Generate humidity based on temperature and weather condition
    let humidity;
    if (weatherCondition.includes('rain') || weatherCondition.includes('snow') || weatherCondition.includes('mist')) {
      humidity = 70 + Math.floor(Math.random() * 25); // 70-95%
    } else if (weatherCondition.includes('cloud')) {
      humidity = 50 + Math.floor(Math.random() * 30); // 50-80%
    } else {
      humidity = 30 + Math.floor(Math.random() * 40); // 30-70%
    }

    // Generate wind speed - higher near poles, lower near equator
    const latFactor = Math.abs(lat) / 90;
    const baseWindSpeed = 2 + (latFactor * 8); // 2-10 m/s base speed
    const windSpeed = baseWindSpeed + (Math.random() * 5 - 2.5); // Add variation

    // Generate wind direction
    const windDirection = Math.floor(Math.random() * 360);

    // Generate atmospheric pressure
    const pressure = 1000 + Math.floor(Math.random() * 30);

    // Calculate feels like temperature based on wind and humidity
    let feelsLike = temperature;

    // Wind chill effect when cold
    if (temperature < 10) {
      feelsLike -= (windSpeed * 0.1);
    }

    // Heat index effect when hot and humid
    if (temperature > 20 && humidity > 60) {
      feelsLike += ((humidity - 60) / 10);
    }

    return {
      name: name,
      main: {
        temp: temperature,
        feels_like: feelsLike,
        humidity: humidity,
        pressure: pressure
      },
      weather: [
        {
          description: weatherCondition,
          icon: weatherIcon
        }
      ],
      wind: {
        speed: windSpeed,
        deg: windDirection,
        gust: windSpeed + (Math.random() * 3)
      },
