        all: weatherCondition.includes('cloud') ? 
          (weatherCondition.includes('few') ? 25 : 
           weatherCondition.includes('scattered') ? 50 : 
           weatherCondition.includes('broken') ? 75 : 90) : 
          Math.floor(Math.random() * 20)
      },
      visibility: weatherCondition.includes('mist') ? 1000 + Math.random() * 4000 : 10000,
      rain_chance: weatherCondition.includes('rain') ? 60 + Math.random() * 40 : 
