    // Weather conditions based on temperature
    let weatherCondition, weatherIcon;
    if (temperature < 0) {
      weatherCondition = 'snow';
      weatherIcon = '13d';
    } else if (temperature < 10) {
      weatherCondition = 'few clouds';
      weatherIcon = '02d';
    } else if (temperature < 20) {
      weatherCondition = 'scattered clouds';
      weatherIcon = '03d';
    } else if (temperature < 30) {
      weatherCondition = 'clear sky';
      weatherIcon = '01d';
    } else {
      weatherCondition = 'haze';
      weatherIcon = '50d';
    }

    return {
      name: name,
      main: {
        temp: temperature,
        feels_like: temperature - 2 + Math.random() * 4,
        humidity: 30 + Math.floor(Math.random() * 70),
        pressure: 1000 + Math.floor(Math.random() * 30)
      },
      weather: [
        {
          description: weatherCondition,
          icon: weatherIcon
        }
