import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMapEvents, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import '../styles/GlobalTemperatureMap.css';

// Fix for Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Custom temperature icon based on temperature value
const getTemperatureIcon = (temperature) => {
  // Define colors based on temperature ranges
  let color;
  if (temperature < -10) color = '#0022ff'; // Very cold
  else if (temperature < 0) color = '#0066ff'; // Cold
  else if (temperature < 10) color = '#00aaff'; // Cool
  else if (temperature < 20) color = '#00ffaa'; // Mild
  else if (temperature < 30) color = '#ffaa00'; // Warm
  else if (temperature < 40) color = '#ff6600'; // Hot
  else color = '#ff0000'; // Very hot

  return L.divIcon({
    className: 'temperature-icon',
    html: `<div style="background-color: ${color}; width: 100%; height: 100%; border-radius: 50%; display: flex; align-items: center; justify-content: center; color: white; font-weight: bold;">${Math.round(temperature)}°</div>`,
    iconSize: [30, 30],
    iconAnchor: [15, 15],
    popupAnchor: [0, -15]
  });
};

// Component to handle map clicks and fetch weather data
const MapClickHandler = ({ onMapClick }) => {
  useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      onMapClick(lat, lng);
    }
  });
  return null;
};

const GlobalTemperatureMap = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedCity, setSelectedCity] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const mapRef = useRef(null);

  // Major cities with their coordinates for initial display
  const majorCities = [
    { name: 'New York', lat: 40.7128, lng: -74.0060 },
    { name: 'London', lat: 51.5074, lng: -0.1278 },
    { name: 'Tokyo', lat: 35.6762, lng: 139.6503 },
    { name: 'Sydney', lat: -33.8688, lng: 151.2093 },
    { name: 'Cairo', lat: 30.0444, lng: 31.2357 },
    { name: 'Rio de Janeiro', lat: -22.9068, lng: -43.1729 },
    { name: 'Moscow', lat: 55.7558, lng: 37.6173 },
    { name: 'Delhi', lat: 28.6139, lng: 77.2090 }
  ];

  // Fetch weather data for initial cities on component mount
  useEffect(() => {
    const fetchInitialWeatherData = async () => {
      setLoading(true);
      try {
        const promises = majorCities.map(city => 
          fetchWeatherForLocation(city.lat, city.lng, city.name)
        );

        const results = await Promise.all(promises);
        setWeatherData(results.filter(data => data !== null));
        setLoading(false);
      } catch (err) {
        console.error('Error fetching initial weather data:', err);
        setError('Failed to load initial weather data');
        setLoading(false);
      }
    };

    fetchInitialWeatherData();
  }, []);

  // Function to fetch weather data for a specific location
  const fetchWeatherForLocation = async (lat, lng, cityName = null) => {
    try {
      // In a real app, you would use an actual API key
      // For demo purposes, we're using OpenWeatherMap API
      const apiKey = 'demo_key'; // Replace with your actual API key in production

      // For development, we'll use mock data instead of making actual API calls
      // In production, uncomment the following line and use your actual API key
      // const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${apiKey}`);

      // Mock response for development
      const mockResponse = getMockWeatherData(lat, lng, cityName);

      return {
        id: Date.now() + Math.random(),
        location: {
          name: mockResponse.name,
          lat: lat,
          lng: lng
        },
        weather: {
          temperature: mockResponse.main.temp,
          feelsLike: mockResponse.main.feels_like,
          humidity: mockResponse.main.humidity,
          pressure: mockResponse.main.pressure,
          description: mockResponse.weather[0].description,
          icon: mockResponse.weather[0].icon
        },
        wind: {
          speed: mockResponse.wind.speed,
          direction: mockResponse.wind.deg
        },
        timestamp: new Date().toISOString()
      };
    } catch (err) {
      console.error('Error fetching weather data:', err);
      return null;
    }
  };

  // Function to handle map clicks
  const handleMapClick = async (lat, lng) => {
    setLoading(true);
    try {
      const newWeatherData = await fetchWeatherForLocation(lat, lng);
      if (newWeatherData) {
        setWeatherData(prevData => [...prevData, newWeatherData]);
        setSelectedCity(newWeatherData);
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching weather data:', err);
      setError('Failed to load weather data for this location');
      setLoading(false);
    }
  };

  // Function to generate mock weather data based on latitude and longitude
  const getMockWeatherData = (lat, lng, cityName = null) => {
    // Generate realistic temperature based on latitude (equator is hotter)
    const baseTemp = 15; // Base temperature
    const latEffect = Math.abs(lat) / 90 * 30; // Temperature decreases as we move away from equator
    const randomVariation = Math.random() * 10 - 5; // Random variation between -5 and 5
    const temperature = baseTemp - latEffect + randomVariation;

    // Generate city name if not provided
    const name = cityName || `Location (${lat.toFixed(2)}, ${lng.toFixed(2)})`;

    // Weather conditions based on temperature
    let weatherCondition, weatherIcon;
    if (temperature < 0) {
      weatherCondition = 'snow';
      weatherIcon = '13d';
    } else if (temperature < 10) {
      weatherCondition = 'few clouds';
      weatherIcon = '02d';
    } else if (temperature < 20) {
      weatherCondition = 'scattered clouds';
      weatherIcon = '03d';
    } else if (temperature < 30) {
      weatherCondition = 'clear sky';
      weatherIcon = '01d';
    } else {
      weatherCondition = 'haze';
      weatherIcon = '50d';
    }

    return {
      name: name,
      main: {
        temp: temperature,
        feels_like: temperature - 2 + Math.random() * 4,
        humidity: 30 + Math.floor(Math.random() * 70),
        pressure: 1000 + Math.floor(Math.random() * 30)
      },
      weather: [
        {
          description: weatherCondition,
          icon: weatherIcon
        }
      ],
      wind: {
        speed: 1 + Math.random() * 9,
        deg: Math.floor(Math.random() * 360)
      }
    };
  };

  // Function to get wind direction as text
  const getWindDirection = (degrees) => {
    const directions = ['N', 'NE', 'E', 'SE', 'S', 'SW', 'W', 'NW'];
    const index = Math.round(degrees / 45) % 8;
    return directions[index];
  };

  // Function to format date
  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString();
  };

  // Function to get temperature color for the circle overlay
  const getTemperatureColor = (temp) => {
    if (temp < -10) return '#0022ff';
    if (temp < 0) return '#0066ff';
    if (temp < 10) return '#00aaff';
    if (temp < 20) return '#00ffaa';
    if (temp < 30) return '#ffaa00';
    if (temp < 40) return '#ff6600';
    return '#ff0000';
  };

  // Function to handle search for places
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    setError(null);

    try {
      // In a real app, you would use an actual geocoding API like OpenStreetMap Nominatim or Google Places API
      // For demo purposes, we'll use a mock geocoding function
      const results = await mockGeocodingSearch(searchQuery);

      if (results.length === 0) {
        setError(`No results found for "${searchQuery}". Try a different search term.`);
      } else {
        setSearchResults(results);
      }

      setIsSearching(false);
    } catch (err) {
      console.error('Error searching for location:', err);
      setError('Failed to search for this location. Please try again.');
      setIsSearching(false);
    }
  };

  // Mock geocoding function - in production, replace with actual API call
  const mockGeocodingSearch = async (query) => {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 800));

    // Generate mock results based on query
    // In a real app, you would call a geocoding API like:
    // const response = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}`);

    // Some predefined places for demo
    const predefinedPlaces = [
      { name: 'New York', country: 'United States', lat: 40.7128, lng: -74.0060 },
      { name: 'London', country: 'United Kingdom', lat: 51.5074, lng: -0.1278 },
      { name: 'Tokyo', country: 'Japan', lat: 35.6762, lng: 139.6503 },
