          <MapClickHandler onMapClick={handleMapClick} />

          {/* Highlighted location marker with pulsing effect */}
          {highlightedLocation && (
            <div>
              <Circle
                center={[highlightedLocation.lat, highlightedLocation.lng]}
                radius={30000}
                pathOptions={{
                  color: '#ff4500',
