import React, { useState, useEffect } from 'react';
import '../styles/WeatherNews.css';

const WeatherNews = () => {
  const [news, setNews] = useState([]);
  const [selectedNews, setSelectedNews] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeatherNews = () => {
      setLoading(true);
      try {
        setTimeout(() => {
          const mockWeatherNews = [
            {
              id: 'news-1',
              title: 'Severe Thunderstorms Expected Across Midwest',
              source: 'Weather Channel',
              url: 'https://weather.com',
              publishedAt: new Date().getTime() - 1 * 60 * 60 * 1000,
              description: 'A line of severe thunderstorms is expected to develop this afternoon, bringing potential for flash flooding and damaging winds.',
              category: 'alert'
            },
            {
              id: 'news-2',
              title: 'Record-Breaking Heat Wave Continues in Southwest',
              source: 'AccuWeather',
              url: 'https://accuweather.com',
              publishedAt: new Date().getTime() - 3 * 60 * 60 * 1000,
              description: 'Temperatures are expected to remain 10-15 degrees above normal through the weekend, with excessive heat warnings in effect.',
              category: 'alert'
            },
            {
              id: 'news-3',
              title: 'Tropical Storm Forms in Atlantic, Monitoring Potential Path',
              source: 'National Hurricane Center',
              url: 'https://nhc.noaa.gov',
              publishedAt: new Date().getTime() - 5 * 60 * 60 * 1000,
              description: 'The season\'s fifth named storm has formed and is currently moving west-northwest at 15 mph.',
              category: 'update'
            },
            {
              id: 'news-4',
              title: 'Study: Climate Change Increasing Frequency of Extreme Weather Events',
              source: 'Science Daily',
              url: 'https://sciencedaily.com',
              publishedAt: new Date().getTime() - 8 * 60 * 60 * 1000,
              description: 'New research shows the rate of extreme weather events has increased by 40% over the past decade.',
              category: 'research'
            },
            {
              id: 'news-5',
              title: 'Heavy Rainfall Causes Flooding in Eastern Coastal Regions',
              source: 'Reuters',
              url: 'https://reuters.com',
              publishedAt: new Date().getTime() - 12 * 60 * 60 * 1000,
              description: 'Several communities have been evacuated as rivers reach record flood stages following days of heavy rainfall.',
              category: 'alert'
            },
            {
              id: 'news-6',
              title: 'Air Quality Alerts Issued for Major Urban Areas',
              source: 'EPA',
              url: 'https://epa.gov',
              publishedAt: new Date().getTime() - 16 * 60 * 60 * 1000,
              description: 'Ozone levels are expected to reach unhealthy levels for sensitive groups today. Residents are advised to limit outdoor activities.',
              category: 'alert'
            },
            {
              id: 'news-7',
              title: 'Drought Conditions Worsen in Agricultural Regions',
              source: 'USDA',
              url: 'https://usda.gov',
              publishedAt: new Date().getTime() - 20 * 60 * 60 * 1000,
              description: 'The latest drought monitor shows expanding severe to extreme drought conditions across key agricultural areas.',
              category: 'update'
            }
          ];
          setNews(mockWeatherNews);
          setLoading(false);
        }, 800);
      } catch (err) {
        console.error('Error fetching weather news:', err);
        setError('Failed to load weather news. Please try again later.');
        setLoading(false);
      }
    };

    fetchWeatherNews();
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatTimeAgo = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now - date;
    const diffSec = Math.round(diffMs / 1000);
    const diffMin = Math.round(diffSec / 60);
    const diffHour = Math.round(diffMin / 60);
    const diffDay = Math.round(diffHour / 24);

    if (diffSec < 60) {
      return `${diffSec} seconds ago`;
    } else if (diffMin < 60) {
      return `${diffMin} minute${diffMin === 1 ? '' : 's'} ago`;
    } else if (diffHour < 24) {
      return `${diffHour} hour${diffHour === 1 ? '' : 's'} ago`;
    } else if (diffDay < 7) {
      return `${diffDay} day${diffDay === 1 ? '' : 's'} ago`;
    } else {
      return date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric'
      });
    }
  };

  const handleNewsClick = (item) => {
    setSelectedNews(item);
  };

  const handleBackClick = () => {
    setSelectedNews(null);
  };

  return (
    <div className="weather-news-container">
      <h3 className="news-header">Weather News</h3>

      {loading && (
        <div className="news-loading">
          <div className="news-spinner"></div>
          <p>Loading weather news...</p>
        </div>
      )}

      {error && (
        <div className="news-error">
          <p>{error}</p>
        </div>
      )}

      {!loading && !error && !selectedNews && (
        <div className="news-list">
          {news.map(item => (
            <div 
              key={item.id} 
              className={`news-item ${item.category}`}
              onClick={() => handleNewsClick(item)}
            >
              <span className="news-category-icon">
                {item.category === 'alert' ? '‚ö†Ô∏è' : 
                 item.category === 'update' ? 'üîÑ' : 'üìä'}
              </span>
              <div className="news-content">
                <h4 className="news-title">{item.title}</h4>
                <p className="news-summary">{item.description}</p>
                <span className="news-source">{item.source} ‚Ä¢ {formatTimeAgo(item.publishedAt)}</span>
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedNews && (
        <div className="news-detail">
          <button className="back-button" onClick={handleBackClick}>
            ‚Üê Back to news
          </button>

          <h3 className="news-detail-title">{selectedNews.title}</h3>

          <div className="news-detail-meta">
            <span className="news-detail-source">{selectedNews.source}</span>
            <span className="news-detail-date">{formatDate(selectedNews.publishedAt)}</span>
          </div>

          <div 
            className="news-detail-content"
            dangerouslySetInnerHTML={{ __html: selectedNews.description }}
          />
        </div>
      )}
    </div>
  );
};

export default WeatherNews;
