import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMapEvents, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import '../styles/GlobalTemperatureMap.css';

// Fix for Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Custom temperature icon based on temperature value
const getTemperatureIcon = (temperature) => {
  // Define colors based on temperature ranges
  let color;
  if (temperature < -10) color = '#0022ff'; // Very cold
  else if (temperature < 0) color = '#0066ff'; // Cold
  else if (temperature < 10) color = '#00aaff'; // Cool
  else if (temperature < 20) color = '#00ffaa'; // Mild
  else if (temperature < 30) color = '#ffaa00'; // Warm
  else if (temperature < 40) color = '#ff6600'; // Hot
  else color = '#ff0000'; // Very hot

  return L.divIcon({
    className: 'temperature-icon',
    html: `<div style="background-color: ${color}; width: 100%; height: 100%; border-radius: 50%; display: flex; align-items: center; justify-content: center; color: white; font-weight: bold;">${Math.round(temperature)}Â°</div>`,
    iconSize: [30, 30],
    iconAnchor: [15, 15],
    popupAnchor: [0, -15]
  });
};

// Component to handle map clicks and fetch weather data
const MapClickHandler = ({ onMapClick }) => {
  useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      onMapClick(lat, lng);
    }
  });
  return null;
};

const GlobalTemperatureMap = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedCity, setSelectedCity] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const mapRef = useRef(null);

  // Major cities with their coordinates for initial display
  const majorCities = [
    { name: 'New York', lat: 40.7128, lng: -74.0060 },
    { name: 'London', lat: 51.5074, lng: -0.1278 },
    { name: 'Tokyo', lat: 35.6762, lng: 139.6503 },
    { name: 'Sydney', lat: -33.8688, lng: 151.2093 },
    { name: 'Cairo', lat: 30.0444, lng: 31.2357 },
    { name: 'Rio de Janeiro', lat: -22.9068, lng: -43.1729 },
    { name: 'Moscow', lat: 55.7558, lng: 37.6173 },
    { name: 'Delhi', lat: 28.6139, lng: 77.2090 }
  ];

  // Fetch weather data for initial cities on component mount
  useEffect(() => {
    const fetchInitialWeatherData = async () => {
      setLoading(true);
      try {
        const promises = majorCities.map(city => 
          fetchWeatherForLocation(city.lat, city.lng, city.name)
        );

        const results = await Promise.all(promises);
        setWeatherData(results.filter(data => data !== null));
        setLoading(false);
      } catch (err) {
        console.error('Error fetching initial weather data:', err);
        setError('Failed to load initial weather data');
        setLoading(false);
      }
    };

    fetchInitialWeatherData();
  }, []);

  // Function to fetch weather data for a specific location using real OpenWeatherMap API
  const fetchWeatherForLocation = async (lat, lng, cityName = null) => {
    try {
      // OpenWeatherMap API key
      const apiKey = '4d8fb5b93d4af21d66a2948710284366'; // Free API key for demo purposes

      // Fetch current weather data
      const weatherResponse = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${apiKey}`
      );

      // Fetch 5-day forecast data
      const forecastResponse = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lng}&units=metric&appid=${apiKey}`
      );

      // Process the current weather data
      const currentWeather = weatherResponse.data;

      // Process the forecast data
      const forecastData = processForecastData(forecastResponse.data);

      return {
        id: Date.now() + Math.random(),
        location: {
          name: cityName || currentWeather.name,
          lat: lat,
          lng: lng
        },
        weather: {
          temperature: currentWeather.main.temp,
          feelsLike: currentWeather.main.feels_like,
          humidity: currentWeather.main.humidity,
          pressure: currentWeather.main.pressure,
          description: currentWeather.weather[0].description,
          icon: currentWeather.weather[0].icon,
          cloudiness: currentWeather.clouds?.all,
          visibility: currentWeather.visibility,
          rainChance: forecastData.length > 0 ? forecastData[0].rainChance : 0
        },
        wind: {
          speed: currentWeather.wind.speed,
          direction: currentWeather.wind.deg,
          gust: currentWeather.wind.gust
        },
        sun: {
          sunrise: new Date(currentWeather.sys.sunrise * 1000).toISOString(),
          sunset: new Date(currentWeather.sys.sunset * 1000).toISOString()
        },
        timestamp: new Date().toISOString(),
        forecast: forecastData
      };
    } catch (err) {
      console.error('Error fetching weather data:', err);
      alert('Error fetching real-time weather data. Please try again later.');
      return null;
    }
  };

  // Function to process forecast data from OpenWeatherMap API
  const processForecastData = (forecastData) => {
    // OpenWeatherMap returns forecast in 3-hour intervals
    // We need to extract one forecast per day
    const dailyForecasts = [];
    const processedDays = new Set();

    // Start from tomorrow (index 8 is 24 hours from now in 3-hour intervals)
    for (let i = 8; i < forecastData.list.length; i += 8) {
      const forecast = forecastData.list[i];
      const date = new Date(forecast.dt * 1000);
      const dateString = date.toISOString().split('T')[0];

      // Skip if we already have a forecast for this day
      if (processedDays.has(dateString)) continue;
      processedDays.add(dateString);

      // Find min and max temperatures for the day
      let minTemp = forecast.main.temp;
      let maxTemp = forecast.main.temp;
      let rainChance = 0;

      // Look at all forecasts for this day
      for (let j = 0; j < forecastData.list.length; j++) {
        const item = forecastData.list[j];
        const itemDate = new Date(item.dt * 1000);
        const itemDateString = itemDate.toISOString().split('T')[0];

        if (itemDateString === dateString) {
          minTemp = Math.min(minTemp, item.main.temp);
          maxTemp = Math.max(maxTemp, item.main.temp);

          // Calculate rain chance based on weather condition
          if (item.weather[0].main === 'Rain' || item.weather[0].main === 'Drizzle') {
            rainChance = Math.max(rainChance, 80);
          } else if (item.weather[0].main === 'Clouds' && item.clouds.all > 60) {
            rainChance = Math.max(rainChance, 40);
          } else if (item.weather[0].main === 'Clouds') {
            rainChance = Math.max(rainChance, 20);
          }
        }
      }

      dailyForecasts.push({
        date: dateString,
        day: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][date.getDay()],
        temp: {
          day: forecast.main.temp,
          night: forecast.main.temp_min, // Approximate
          min: minTemp,
          max: maxTemp
        },
        weather: forecast.weather[0],
        humidity: forecast.main.humidity,
        windSpeed: forecast.wind.speed,
        rainChance: rainChance
      });

      // Limit to 5 days
      if (dailyForecasts.length >= 5) break;
    }

    return dailyForecasts;
  };

  // Function to generate a mock weather forecast for the next few days
  const generateMockForecast = (lat, lng) => {
    const forecast = [];
    const currentDate = new Date();
    const baseTemp = getBaseTemperatureForLocation(lat);

    // Generate forecast for next 5 days
    for (let i = 1; i <= 5; i++) {
      const forecastDate = new Date(currentDate);
      forecastDate.setDate(forecastDate.getDate() + i);

      // Add some random variation to temperature
      const tempVariation = Math.random() * 6 - 3; // -3 to +3 degrees
      const dayTemp = baseTemp + tempVariation;
      const nightTemp = dayTemp - (5 + Math.random() * 3); // 5-8 degrees cooler at night

      // Randomize weather conditions
      const conditions = [
        { description: 'clear sky', icon: '01d' },
        { description: 'few clouds', icon: '02d' },
        { description: 'scattered clouds', icon: '03d' },
        { description: 'broken clouds', icon: '04d' },
        { description: 'shower rain', icon: '09d' },
        { description: 'rain', icon: '10d' },
        { description: 'thunderstorm', icon: '11d' },
        { description: 'snow', icon: '13d' },
        { description: 'mist', icon: '50d' }
      ];

      // Weight the conditions based on temperature
      let conditionIndex;
      if (dayTemp < 0) {
        // More likely to be snow or mist when cold
        conditionIndex = Math.floor(Math.random() * 2) + 6;
      } else if (dayTemp < 10) {
        // More likely to be cloudy or rainy when cool
        conditionIndex = Math.floor(Math.random() * 5) + 2;
      } else if (dayTemp > 30) {
        // More likely to be clear or thunderstorm when hot
        conditionIndex = Math.random() < 0.7 ? 0 : 6;
      } else {
        // Random conditions for mild temperatures
        conditionIndex = Math.floor(Math.random() * conditions.length);
      }

      forecast.push({
        date: forecastDate.toISOString().split('T')[0],
        day: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][forecastDate.getDay()],
        temp: {
          day: dayTemp,
          night: nightTemp,
          min: nightTemp - Math.random() * 2,
          max: dayTemp + Math.random() * 2
        },
        weather: conditions[conditionIndex],
        humidity: Math.floor(Math.random() * 40) + 40, // 40-80%
        windSpeed: 2 + Math.random() * 8, // 2-10 m/s
        rainChance: Math.floor(Math.random() * 100) // 0-100%
      });
    }

    return forecast;
  };

  // Function to get base temperature based on latitude (simulating real-world climate patterns)
  const getBaseTemperatureForLocation = (lat) => {
    // Get current month (0-11)
    const currentMonth = new Date().getMonth();

    // Northern hemisphere summer / Southern hemisphere winter
    const isSummerNorth = currentMonth >= 4 && currentMonth <= 8;

    // Calculate base temperature based on latitude and season
    const equatorTemp = 30; // Base temperature at equator
    const poleTemp = isSummerNorth ? -10 : -30; // Base temperature at poles

    // Adjust for hemisphere and season
    const absLat = Math.abs(lat);
    const latFactor = absLat / 90;

    if ((lat >= 0 && isSummerNorth) || (lat < 0 && !isSummerNorth)) {
      // Summer in this hemisphere
      return equatorTemp - (latFactor * (equatorTemp - poleTemp + 15));
    } else {
      // Winter in this hemisphere
      return equatorTemp - (latFactor * (equatorTemp - poleTemp));
    }
  };

  // Function to generate realistic weather data based on location and current time
  const getRealTimeWeatherData = (lat, lng, cityName = null) => {
    // Generate realistic temperature based on latitude, longitude, and current season
    const baseTemp = getBaseTemperatureForLocation(lat);

    // Add daily variation based on current hour
    const hour = new Date().getHours();
    let timeOfDayVariation = 0;

    // Temperature varies throughout the day
    if (hour >= 6 && hour < 12) {
      // Morning - temperature rising
      timeOfDayVariation = (hour - 6) * 0.5;
    } else if (hour >= 12 && hour < 18) {
      // Afternoon - warmest part of day
      timeOfDayVariation = 3 - (Math.abs(hour - 15) * 0.3);
    } else if (hour >= 18 && hour < 24) {
      // Evening - temperature falling
      timeOfDayVariation = 2 - ((hour - 18) * 0.5);
    } else {
      // Night - coolest part of day
      timeOfDayVariation = -2;
    }

    // Add random variation and time of day effect
    const randomVariation = Math.random() * 4 - 2; // Random variation between -2 and 2
    const temperature = baseTemp + timeOfDayVariation + randomVariation;

    // Generate city name if not provided
    const name = cityName || `Location (${lat.toFixed(2)}, ${lng.toFixed(2)})`;

    // Weather conditions based on temperature and randomness
    let weatherCondition, weatherIcon;
    const rand = Math.random();

    if (temperature < -5) {
      if (rand < 0.7) {
        weatherCondition = 'snow';
        weatherIcon = '13d';
      } else {
        weatherCondition = 'mist';
        weatherIcon = '50d';
      }
    } else if (temperature < 5) {
      if (rand < 0.4) {
        weatherCondition = 'light snow';
        weatherIcon = '13d';
      } else if (rand < 0.7) {
        weatherCondition = 'mist';
        weatherIcon = '50d';
      } else {
        weatherCondition = 'overcast clouds';
        weatherIcon = '04d';
      }
    } else if (temperature < 15) {
      if (rand < 0.3) {
        weatherCondition = 'light rain';
        weatherIcon = '10d';
      } else if (rand < 0.6) {
        weatherCondition = 'broken clouds';
        weatherIcon = '04d';
      } else if (rand < 0.9) {
        weatherCondition = 'scattered clouds';
        weatherIcon = '03d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    } else if (temperature < 25) {
      if (rand < 0.2) {
        weatherCondition = 'light rain';
        weatherIcon = '10d';
      } else if (rand < 0.5) {
        weatherCondition = 'few clouds';
        weatherIcon = '02d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    } else if (temperature < 35) {
      if (rand < 0.15) {
        weatherCondition = 'thunderstorm';
        weatherIcon = '11d';
      } else if (rand < 0.3) {
        weatherCondition = 'few clouds';
        weatherIcon = '02d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    } else {
      if (rand < 0.2) {
        weatherCondition = 'thunderstorm';
        weatherIcon = '11d';
      } else {
        weatherCondition = 'clear sky';
        weatherIcon = '01d';
      }
    }

    // Adjust icon for day/night
    if (hour >= 18 || hour < 6) {
      weatherIcon = weatherIcon.replace('d', 'n');
    }

    // Generate humidity based on temperature and weather condition
    let humidity;
    if (weatherCondition.includes('rain') || weatherCondition.includes('snow') || weatherCondition.includes('mist')) {
      humidity = 70 + Math.floor(Math.random() * 25); // 70-95%
    } else if (weatherCondition.includes('cloud')) {
      humidity = 50 + Math.floor(Math.random() * 30); // 50-80%
    } else {
      humidity = 30 + Math.floor(Math.random() * 40); // 30-70%
    }

    // Generate wind speed - higher near poles, lower near equator
    const latFactor = Math.abs(lat) / 90;
    const baseWindSpeed = 2 + (latFactor * 8); // 2-10 m/s base speed
    const windSpeed = baseWindSpeed + (Math.random() * 5 - 2.5); // Add variation

    // Generate wind direction
    const windDirection = Math.floor(Math.random() * 360);

    // Generate atmospheric pressure
    const pressure = 1000 + Math.floor(Math.random() * 30);

    // Calculate feels like temperature based on wind and humidity
    let feelsLike = temperature;

    // Wind chill effect when cold
    if (temperature < 10) {
      feelsLike -= (windSpeed * 0.1);
    }

    // Heat index effect when hot and humid
    if (temperature > 20 && humidity > 60) {
      feelsLike += ((humidity - 60) / 10);
    }

    return {
      name: name,
      main: {
        temp: temperature,
        feels_like: feelsLike,
        humidity: humidity,
        pressure: pressure
      },
      weather: [
        {
          description: weatherCondition,
          icon: weatherIcon
        }
      ],
      wind: {
        speed: windSpeed,
        deg: windDirection,
        gust: windSpeed + (Math.random() * 3)
      },
      clouds: {
        all: weatherCondition.includes('cloud') ? 
          (weatherCondition.includes('few') ? 25 : 
           weatherCondition.includes('scattered') ? 50 : 
           weatherCondition.includes('broken') ? 75 : 90) : 
          Math.floor(Math.random() * 20)
      },
      visibility: weatherCondition.includes('mist') ? 1000 + Math.random() * 4000 : 10000,
      rain_chance: weatherCondition.includes('rain') ? 60 + Math.random() * 40 : 
                   weatherCondition.includes('cloud') ? 20 + Math.random() * 30 : 
                   Math.random() * 10
    };
  };

  // Function to handle map clicks
  const handleMapClick = async (lat, lng) => {
    setLoading(true);
    try {
      const newWeatherData = await fetchWeatherForLocation(lat, lng);
      if (newWeatherData) {
        setWeatherData(prevData => [...prevData, newWeatherData]);
        setSelectedCity(newWeatherData);
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching weather data:', err);
      setError('Failed to load weather data for this location');
      setLoading(false);
    }
  };

  // Function to generate mock weather data based on latitude and longitude
  const getMockWeatherData = (lat, lng, cityName = null) => {
    // Generate realistic temperature based on latitude (equator is hotter)
    const baseTemp = 15; // Base temperature
    const latEffect = Math.abs(lat) / 90 * 30; // Temperature decreases as we move away from equator
    const randomVariation = Math.random() * 10 - 5; // Random variation between -5 and 5
    const temperature = baseTemp - latEffect + randomVariation;

    // Generate city name if not provided
    const name = cityName || `Location (${lat.toFixed(2)}, ${lng.toFixed(2)})`;

    // Weather conditions based on temperature
    let weatherCondition, weatherIcon;
    if (temperature < 0) {
      weatherCondition = 'snow';
      weatherIcon = '13d';
    } else if (temperature < 10) {
      weatherCondition = 'few clouds';
      weatherIcon = '02d';
    } else if (temperature < 20) {
      weatherCondition = 'scattered clouds';
      weatherIcon = '03d';
    } else if (temperature < 30) {
      weatherCondition = 'clear sky';
      weatherIcon = '01d';
    } else {
      weatherCondition = 'haze';
      weatherIcon = '50d';
    }

    return {
      name: name,
      main: {
        temp: temperature,
        feels_like: temperature - 2 + Math.random() * 4,
        humidity: 30 + Math.floor(Math.random() * 70),
        pressure: 1000 + Math.floor(Math.random() * 30)
      },
      weather: [
        {
          description: weatherCondition,
          icon: weatherIcon
        }
      ],
      wind: {
        speed: 1 + Math.random() * 9,
        deg: Math.floor(Math.random() * 360)
      }
    };
  };

  // Function to get wind direction as text
  const getWindDirection = (degrees) => {
    const directions = ['N', 'NE', 'E', 'SE', 'S', 'SW', 'W', 'NW'];
    const index = Math.round(degrees / 45) % 8;
    return directions[index];
  };

  // Function to format date
  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString();
  };

  // Function to get temperature color for the circle overlay
  const getTemperatureColor = (temp) => {
    if (temp < -10) return '#0022ff';
    if (temp < 0) return '#0066ff';
    if (temp < 10) return '#00aaff';
    if (temp < 20) return '#00ffaa';
    if (temp < 30) return '#ffaa00';
    if (temp < 40) return '#ff6600';
    return '#ff0000';
  };

  // Function to handle search for places using real geocoding API
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    setError(null);

    try {
      // Use OpenStreetMap Nominatim API for geocoding
      const results = await geocodeLocation(searchQuery);

      if (results.length === 0) {
        setError(`No results found for "${searchQuery}". Try a different search term.`);
      } else {
        setSearchResults(results);
      }

      setIsSearching(false);
    } catch (err) {
      console.error('Error searching for location:', err);
      setError('Failed to search for this location. Please try again.');
      setIsSearching(false);
    }
  };

  // Real geocoding function using OpenStreetMap Nominatim API
  const geocodeLocation = async (query) => {
    try {
      // Call the OpenStreetMap Nominatim API
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&limit=5`,
        {
          headers: {
            'User-Agent': 'ClimateChangeApp/1.0' // Required by Nominatim usage policy
          }
        }
      );

      // Process and format the results
      return response.data.map(place => ({
        id: place.place_id,
        name: place.name || extractLocationName(place.display_name),
        country: extractCountry(place.display_name),
        lat: parseFloat(place.lat),
        lng: parseFloat(place.lon),
        display_name: place.display_name
      }));
    } catch (error) {
      console.error('Geocoding error:', error);
      throw new Error('Failed to search for location');
    }
  };

  // Helper function to extract location name from display_name
  const extractLocationName = (displayName) => {
    if (!displayName) return 'Unknown Location';
    const parts = displayName.split(',');
    return parts[0].trim();
  };

  // Helper function to extract country from display_name
  const extractCountry = (displayName) => {
    if (!displayName) return '';
    const parts = displayName.split(',');
    return parts[parts.length - 1].trim();
  };

  // Function to select a search result
  const selectSearchResult = async (result) => {
    setSearchResults([]);
    setSearchQuery('');

    // Center map on selected location
    if (mapRef.current) {
      mapRef.current.setView([result.lat, result.lng], 10);
    }

    // Fetch weather for the selected location
    setLoading(true);
    try {
      const weatherData = await fetchWeatherForLocation(result.lat, result.lng, result.name);
      if (weatherData) {
        setWeatherData(prevData => {
          // Check if we already have weather data for this location
          const existingIndex = prevData.findIndex(item => 
            Math.abs(item.location.lat - result.lat) < 0.01 && 
            Math.abs(item.location.lng - result.lng) < 0.01
          );

          if (existingIndex >= 0) {
            // Update existing data
            const updatedData = [...prevData];
            updatedData[existingIndex] = weatherData;
            return updatedData;
          } else {
            // Add new data
            return [...prevData, weatherData];
          }
        });
        setSelectedCity(weatherData);
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching weather data for search result:', err);
      setError('Failed to load weather data for this location');
      setLoading(false);
    }
  };

  return (
    <div className="global-temperature-map">
      <h2>Global Temperature Map</h2>

      <div className="search-container">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search for any city, town, district, or place..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button" disabled={isSearching}>
            {isSearching ? 'Searching...' : 'Search'}
          </button>
        </form>

        {searchResults.length > 0 && (
          <div className="search-results">
            {searchResults.map(result => (
              <div 
                key={result.id} 
                className="search-result-item"
                onClick={() => selectSearchResult(result)}
              >
                <div className="result-name">{result.name}</div>
                <div className="result-location">{result.display_name}</div>
              </div>
            ))}
          </div>
        )}
      </div>

      <p className="map-instructions">
        Search for any place or click anywhere on the map to see current weather conditions. Spin the globe by dragging.
      </p>

      <div className="map-container-wrapper">
        <MapContainer
          center={[20, 0]}
          zoom={2}
          minZoom={2}
          maxBounds={[[-90, -180], [90, 180]]}
          scrollWheelZoom={true}
          className="globe-map-container"
          ref={mapRef}
          worldCopyJump={true}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          <MapClickHandler onMapClick={handleMapClick} />

          {weatherData.map((data) => (
            <React.Fragment key={data.id}>
              <Circle
                center={[data.location.lat, data.location.lng]}
                radius={50000}
                pathOptions={{
                  fillColor: getTemperatureColor(data.weather.temperature),
                  fillOpacity: 0.5,
                  color: getTemperatureColor(data.weather.temperature),
                  weight: 1
                }}
              />
              <Marker
                position={[data.location.lat, data.location.lng]}
                icon={getTemperatureIcon(data.weather.temperature)}
                eventHandlers={{
                  click: () => {
                    setSelectedCity(data);
                  }
                }}
              >
                <Popup>
                  <div className="weather-popup">
                    <h3>{data.location.name}</h3>
                    <div className="weather-main">
                      <div className="temperature">
                        <span className="temp-value">{Math.round(data.weather.temperature)}Â°C</span>
                        <span className="feels-like">Feels like: {Math.round(data.weather.feelsLike)}Â°C</span>
                      </div>
                      <div className="weather-icon">
                        <img 
                          src={`https://openweathermap.org/img/wn/${data.weather.icon}@2x.png`} 
                          alt={data.weather.description}
                        />
                        <span>{data.weather.description}</span>
                      </div>
                    </div>
                    <div className="weather-details">
                      <div className="detail">
                        <span className="label">Humidity:</span>
                        <span className="value">{data.weather.humidity}%</span>
                      </div>
                      <div className="detail">
                        <span className="label">Pressure:</span>
                        <span className="value">{data.weather.pressure} hPa</span>
                      </div>
                      <div className="detail">
                        <span className="label">Wind:</span>
                        <span className="value">
                          {data.wind.speed.toFixed(1)} m/s {getWindDirection(data.wind.direction)}
                        </span>
                      </div>
                      <div className="detail">
                        <span className="label">Updated:</span>
                        <span className="value">{formatDate(data.timestamp)}</span>
                      </div>
                    </div>
                  </div>
                </Popup>
              </Marker>
            </React.Fragment>
          ))}
        </MapContainer>
      </div>

      {loading && (
        <div className="loading-indicator">
          <div className="spinner"></div>
          <p>Loading weather data...</p>
        </div>
      )}

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      {selectedCity && (
        <div className="selected-city-details">
          <h3>Selected Location: {selectedCity.location.name}</h3>
          <div className="weather-card">
            <div className="weather-header">
              <div className="temperature-large">
                {Math.round(selectedCity.weather.temperature)}Â°C
                <span className="feels-like">Feels like: {Math.round(selectedCity.weather.feelsLike)}Â°C</span>
              </div>
              <div className="weather-icon-large">
                <img 
                  src={`https://openweathermap.org/img/wn/${selectedCity.weather.icon}@2x.png`} 
                  alt={selectedCity.weather.description}
                />
                <span>{selectedCity.weather.description}</span>
              </div>
            </div>

            <div className="current-conditions">
              <h4>Current Conditions</h4>
              <div className="weather-details-grid">
                <div className="detail-item">
                  <span className="detail-icon">ð§</span>
                  <span className="detail-label">Humidity</span>
                  <span className="detail-value">{selectedCity.weather.humidity}%</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ð¡ï¸</span>
                  <span className="detail-label">Pressure</span>
                  <span className="detail-value">{selectedCity.weather.pressure} hPa</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ð¨</span>
                  <span className="detail-label">Wind Speed</span>
                  <span className="detail-value">{selectedCity.wind.speed.toFixed(1)} m/s</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ð§­</span>
                  <span className="detail-label">Wind Direction</span>
                  <span className="detail-value">{getWindDirection(selectedCity.wind.direction)}</span>
                </div>
                {selectedCity.wind.gust && (
                  <div className="detail-item">
                    <span className="detail-icon">ð¨</span>
                    <span className="detail-label">Wind Gusts</span>
                    <span className="detail-value">{selectedCity.wind.gust.toFixed(1)} m/s</span>
                  </div>
                )}
                {selectedCity.weather.cloudiness !== undefined && (
                  <div className="detail-item">
                    <span className="detail-icon">âï¸</span>
                    <span className="detail-label">Cloudiness</span>
                    <span className="detail-value">{selectedCity.weather.cloudiness}%</span>
                  </div>
                )}
                {selectedCity.weather.visibility !== undefined && (
                  <div className="detail-item">
                    <span className="detail-icon">ðï¸</span>
                    <span className="detail-label">Visibility</span>
                    <span className="detail-value">
                      {selectedCity.weather.visibility >= 10000 
                        ? '10+ km' 
                        : `${(selectedCity.weather.visibility / 1000).toFixed(1)} km`}
                    </span>
                  </div>
                )}
                {selectedCity.weather.rainChance !== undefined && (
                  <div className="detail-item">
                    <span className="detail-icon">ð§ï¸</span>
                    <span className="detail-label">Chance of Rain</span>
                    <span className="detail-value">{Math.round(selectedCity.weather.rainChance)}%</span>
                  </div>
                )}
                {selectedCity.sun && (
                  <>
                    <div className="detail-item">
                      <span className="detail-icon">ð</span>
                      <span className="detail-label">Sunrise</span>
                      <span className="detail-value">{new Date(selectedCity.sun.sunrise).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-icon">ð</span>
                      <span className="detail-label">Sunset</span>
                      <span className="detail-value">{new Date(selectedCity.sun.sunset).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</span>
                    </div>
                  </>
                )}
                <div className="detail-item">
                  <span className="detail-icon">ð</span>
                  <span className="detail-label">Coordinates</span>
                  <span className="detail-value">
                    {selectedCity.location.lat.toFixed(4)}, {selectedCity.location.lng.toFixed(4)}
                  </span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ð</span>
                  <span className="detail-label">Updated</span>
                  <span className="detail-value">{formatDate(selectedCity.timestamp)}</span>
                </div>
              </div>
            </div>

            {selectedCity.forecast && selectedCity.forecast.length > 0 && (
              <div className="forecast-section">
                <h4>5-Day Forecast</h4>
                <div className="forecast-container">
                  {selectedCity.forecast.map((day, index) => (
                    <div key={day.date} className="forecast-day">
                      <div className="forecast-date">{index === 0 ? 'Tomorrow' : day.day}</div>
                      <div className="forecast-icon">
                        <img 
                          src={`https://openweathermap.org/img/wn/${day.weather.icon}@2x.png`} 
                          alt={day.weather.description}
                        />
                      </div>
                      <div className="forecast-temp">
                        <span className="temp-high">{Math.round(day.temp.max)}Â°</span>
                        <span className="temp-low">{Math.round(day.temp.min)}Â°</span>
                      </div>
                      <div className="forecast-desc">{day.weather.description}</div>
                      <div className="forecast-details">
                        <div className="forecast-detail">
                          <span className="detail-icon small">ð§</span>
                          <span className="detail-value small">{day.humidity}%</span>
                        </div>
                        <div className="forecast-detail">
                          <span className="detail-icon small">ð¨</span>
                          <span className="detail-value small">{day.windSpeed.toFixed(1)} m/s</span>
                        </div>
                        <div className="forecast-detail">
                          <span className="detail-icon small">ð§ï¸</span>
                          <span className="detail-value small">{day.rainChance}%</span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      <div className="temperature-legend">
        <h4>Temperature Scale (Â°C)</h4>
        <div className="legend-gradient">
          <div className="gradient-bar"></div>
          <div className="gradient-labels">
            <span>-10Â°</span>
            <span>0Â°</span>
            <span>10Â°</span>
            <span>20Â°</span>
            <span>30Â°</span>
            <span>40Â°+</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GlobalTemperatureMap;
